import React, { useState } from 'react';
import './About.css';

const ProductSlider = () => {
  // Product data with full details
  const products = [
    {
      id: 1,
      name: "Tech Gadget Pro",
      price: "$299.99",
      image: "https://via.placeholder.com/300x300?text=Product+1",
      description: "Advanced technology for everyday use"
    },
    {
      id: 2,
      name: "Smart Home Hub",
      price: "$199.50",
      image: "https://via.placeholder.com/300x300?text=Product+2",
      description: "Connect and control your smart devices"
    },
    {
      id: 3,
      name: "Wireless Earbuds",
      price: "$149.99",
      image: "https://via.placeholder.com/300x300?text=Product+3",
      description: "Crystal clear sound, ultimate comfort"
    },
    {
      id: 4,
      name: "Laptop Companion",
      price: "$449.99",
      image: "https://via.placeholder.com/300x300?text=Product+4",
      description: "Powerful performance in a sleek design"
    },
    {
      id: 5,
      name: "Gaming Mouse",
      price: "$79.99",
      image: "https://via.placeholder.com/300x300?text=Product+5",
      description: "Precision engineering for gamers"
    },
    {
      id: 6,
      name: "Portable Charger",
      price: "$59.99",
      image: "https://via.placeholder.com/300x300?text=Product+6",
      description: "Never run out of battery again"
    }
  ];

  // State for current slide
  const [currentSlide, setCurrentSlide] = useState(0);

  // Handle next slide
  const handleNextSlide = () => {
    setCurrentSlide((prev) => (prev === 1 ? 0 : 1));
  };

  // Handle previous slide
  const handlePrevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? 1 : 0));
  };

  return (
    <>
    <section id="about">
        <section className="wrapper">
            <section class="about">
            <div class="head">
                <h3>What Our Customer Say About Us</h3>
                        <div class="right">
                            <img class="left-arrow" src={LeftArrow} alt='Left-Arrow' />
                            <img class="right-arrow" src={RightArrow} alt='Right-Arrow' />
                        </div>
                    </div>
            </section>
        </section>
    </section>


    <div className="product-slider-container">
      {/* Navigation Left Button */}
      <button 
        className="slider-nav-btn left-btn"
        onClick={handlePrevSlide}
      >
        ◀
      </button>

      {/* Product Slider */}
      <div className="product-slider">
        <div 
          className="product-track"
          style={{
            transform: `translateX(-${currentSlide * 50}%)`,
            transition: 'transform 0.5s ease'
          }}
        >
          {/* First Slide */}
          <div className="product-slide">
            {products.slice(0, 3).map((product) => (
              <div key={product.id} className="product-card">
                <img src={product.image} alt={product.name} />
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <div className="product-footer">
                  <span className="price">{product.price}</span>
                  <button>Add to Cart</button>
                </div>
              </div>
            ))}
          </div>

          {/* Second Slide */}
          <div className="product-slide">
            {products.slice(3, 6).map((product) => (
              <div key={product.id} className="product-card">
                <img src={product.image} alt={product.name} />
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <div className="product-footer">
                  <span className="price">{product.price}</span>
                  <button>Add to Cart</button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Navigation Right Button */}
      <button 
        className="slider-nav-btn right-btn"
        onClick={handleNextSlide}
      >
        ▶
      </button>

      {/* Slide Indicators */}
      <div className="slide-indicators">
        {[0, 1].map((index) => (
          <span 
            key={index}
            className={`indicator ${currentSlide === index ? 'active' : ''}`}
            onClick={() => setCurrentSlide(index)}
          />
        ))}
      </div>
    </div>
    </>
  );
};

export default ProductSlider;
