import React, { useState, useEffect } from 'react'
import './About.css'
import LeftArrow from "../assets/icons/short-arrow-left.svg"
import RightArrow from "../assets/icons/short-arrow-right.svg"
import QuoteIcon from "../assets/icons/quotes.svg"
import UserImage from "../assets/icons/span-7.svg"

function About() {
  // Testimonials data
  const testimonials = [
    {
      quote: "Byway's tech courses are top-notch! As someone who's always looking to stay ahead in the rapidly evolvin",
      name: "Jane Doe",
      role: "Designer",
      image: UserImage
    },
    {
      quote: "An incredible learning platform that offers practical, cutting-edge technology courses. The curriculum is des",
      name: "John Smith",
      role: "Software Engineer",
      image: UserImage
    },
    {
      quote: "Byway has transformed my understanding of tech. The courses are comprehensive, engaging, and truly practical.",
      name: "Emily Wong",
      role: "Product Manager",
      image: UserImage
    },
    {
      quote: "A game-changing educational platform that provides real-world skills and insights into the latest technolog",
      name: "Michael Chen",
      role: "Data Scientist",
      image: UserImage
    },
    {
      quote: "The most up-to-date and interactive tech learning experience I've ever encountered. Highly recommended!",
      name: "Sarah Johnson",
      role: "UX Designer",
      image: UserImage
    },
    {
      quote: "Byway's approach to tech education is revolutionary. The depth and quality of content are unmatched.",
      name: "David Kim",
      role: "Cloud Architect",
      image: UserImage
    }
  ];

  // Calculate total number of slides
  const totalSlides = Math.ceil(testimonials.length / 3);

  // State management
  const [currentSlide, setCurrentSlide] = useState(0);
  const [slideDirection, setSlideDirection] = useState(null);
  const [isTransitioning, setIsTransitioning] = useState(false);

  // Slide navigation functions
  const handleNextSlide = () => {
    if (isTransitioning || currentSlide === totalSlides - 1) return;

    setSlideDirection('next');
    setIsTransitioning(true);
  };

  const handlePrevSlide = () => {
    if (isTransitioning || currentSlide === 0) return;

    setSlideDirection('prev');
    setIsTransitioning(true);
  };

  // UseEffect to handle slide transitions
  useEffect(() => {
    if (isTransitioning) {
      const timer = setTimeout(() => {
        if (slideDirection === 'next') {
          setCurrentSlide((prev) => Math.min(prev + 1, totalSlides - 1));
        } else if (slideDirection === 'prev') {
          setCurrentSlide((prev) => Math.max(prev - 1, 0));
        }
        
        setIsTransitioning(false);
        setSlideDirection(null);
      }, 500); // Match the CSS animation duration

      return () => clearTimeout(timer);
    }
  }, [isTransitioning, slideDirection, totalSlides]);

  // Get current slide's testimonials
  const getSlideTestimonials = () => {
    const startIndex = currentSlide * 3;
    return testimonials.slice(startIndex, startIndex + 3);
  };

  return (
    <section id="about">
      <section className="wrapper">
        <section className="about">
          <div className="head">
            <h3>What Our Customer Say About Us</h3>
            <div className="right">
            <img 
                className={`left-arrow navigation-arrow ${currentSlide === 0 ? 'disabled' : ''}`}
                src={LeftArrow} 
                alt='Left-Arrow' 
                onClick={handlePrevSlide}
                style={{
                  cursor: currentSlide === 0 ? 'default' : 'pointer',
                  backgroundColor: currentSlide === 0 ,
                  pointerEvents: currentSlide === 0 ? 'none' : 'auto'
                }}
              />
              <img 
                className={`right-arrow navigation-arrow ${currentSlide === totalSlides - 1 ? 'disabled' : ''}`}
                src={RightArrow} 
                alt='Right-Arrow' 
                onClick={handleNextSlide}
                style={{
                  cursor: currentSlide === totalSlides - 1 ? 'default' : 'pointer',
                  backgroundColor: currentSlide === totalSlides - 1 ,
                  pointerEvents: currentSlide === totalSlides - 1 ? 'none' : 'auto'
                }}
              />
            </div>
          </div>
          <div className="body">
            <div 
              className={`testimonial-slider 
                ${isTransitioning ? `slide-${slideDirection}` : ''}`}
            >
              <div className="testimonial-track">
                <ul 
                  style={{
                    transform: isTransitioning 
                      ? (slideDirection === 'next' 
                          ? 'translateX(-100%)' 
                          : 'translateX(100%)')
                      : 'translateX(0)'
                  }}
                >
                  {getSlideTestimonials().map((testimonial, index) => (
                    <li key={index}>
                      <img src={QuoteIcon} alt='Quote' />
                      <p>"{testimonial.quote}"</p>
                      <div className="role">
                        <img src={testimonial.image} alt='User-Image' />
                        <div className="name">
                          <h4>{testimonial.name}</h4>
                          <small>{testimonial.role}</small>
                        </div>
                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </section>
      </section>
    </section>
  )
}

export default About
