import React, { useState, useEffect } from 'react'
import './About.css'
import LeftArrow from "../assets/icons/short-arrow-left.svg"
import RightArrow from "../assets/icons/short-arrow-right.svg"
import QuoteIcon from "../assets/icons/quotes.svg"
import UserImage from "../assets/icons/span-7.svg"

function About() {
  // ... (previous testimonials and state management code remains the same)

  return (
    <section id="about">
      <section className="wrapper">
        <section className="about">
          <div className="head">
            <h3>What Our Customer Say About Us</h3>
            <div className="right">
              <img 
                className={`left-arrow navigation-arrow ${currentSlide === 0 ? 'disabled' : ''}`}
                src={LeftArrow} 
                alt='Left-Arrow' 
                onClick={handlePrevSlide}
                style={{
                  cursor: currentSlide === 0 ? 'default' : 'pointer',
                  backgroundColor: currentSlide === 0 ? '#E2E8F0' : '#94A3B8',
                  pointerEvents: currentSlide === 0 ? 'none' : 'auto'
                }}
              />
              <img 
                className={`right-arrow navigation-arrow ${currentSlide === totalSlides - 1 ? 'disabled' : ''}`}
                src={RightArrow} 
                alt='Right-Arrow' 
                onClick={handleNextSlide}
                style={{
                  cursor: currentSlide === totalSlides - 1 ? 'default' : 'pointer',
                  backgroundColor: currentSlide === totalSlides - 1 ? '#E2E8F0' : '#94A3B8',
                  pointerEvents: currentSlide === totalSlides - 1 ? 'none' : 'auto'
                }}
              />
            </div>
          </div>
          {/* ... rest of the component remains the same */}
        </section>
      </section>
    </section>
  )
}

export default About
