import React, { useState } from 'react'
import LeftArrow from "../assets/icons/short-arrow-left.svg"
import RightArrow from "../assets/icons/short-arrow-right.svg"
import QuoteIcon from "../assets/icons/quotes.svg"
import UserImage from "../assets/icons/span-7.svg"

function About() {
  // Testimonials data
  const testimonials = [
    {
      quote: "Byway's tech courses are top-notch! As someone who's always looking to stay ahead in the rapidly evolving tech world, I appreciate the up-to-date content and engaging multimedia.",
      name: "Jane Doe",
      role: "Designer",
      image: UserImage
    },
    {
      quote: "An incredible learning platform that offers practical, cutting-edge technology courses. The curriculum is designed to keep you ahead of the curve.",
      name: "John Smith",
      role: "Software Engineer",
      image: UserImage
    },
    {
      quote: "Byway has transformed my understanding of tech. The courses are comprehensive, engaging, and truly practical.",
      name: "Emily Wong",
      role: "Product Manager",
      image: UserImage
    },
    {
      quote: "A game-changing educational platform that provides real-world skills and insights into the latest technological trends.",
      name: "Michael Chen",
      role: "Data Scientist",
      image: UserImage
    },
    {
      quote: "The most up-to-date and interactive tech learning experience I've ever encountered. Highly recommended!",
      name: "Sarah Johnson",
      role: "UX Designer",
      image: UserImage
    },
    {
      quote: "Byway's approach to tech education is revolutionary. The depth and quality of content are unmatched.",
      name: "David Kim",
      role: "Cloud Architect",
      image: UserImage
    }
  ];

  // State to manage current slide
  const [currentSlide, setCurrentSlide] = useState(0);

  // Function to handle next slide
  const handleNextSlide = () => {
    setCurrentSlide((prevSlide) => 
      (prevSlide + 1) % Math.ceil(testimonials.length / 3)
    );
  };

  // Function to handle previous slide
  const handlePrevSlide = () => {
    setCurrentSlide((prevSlide) => 
      prevSlide === 0 
        ? Math.ceil(testimonials.length / 3) - 1 
        : prevSlide - 1
    );
  };

  // Get current slide's testimonials
  const getSlideTestimonials = () => {
    const startIndex = currentSlide * 3;
    return testimonials.slice(startIndex, startIndex + 3);
  };

  return (
    <>
      <section id="about">
        <section className="wrapper">
          <section className="about">
            <div className="head">
              <h3>What Our Customer Say About Us</h3>
              <div className="right">
                <img 
                  className="left-arrow" 
                  src={LeftArrow} 
                  alt='Left-Arrow' 
                  onClick={handlePrevSlide}
                  style={{cursor: 'pointer'}}
                />
                <img 
                  className="right-arrow" 
                  src={RightArrow} 
                  alt='Right-Arrow' 
                  onClick={handleNextSlide}
                  style={{cursor: 'pointer'}}
                />
              </div>
            </div>
            <div className="body">
              <ul>
                {getSlideTestimonials().map((testimonial, index) => (
                  <li key={index}>
                    <img src={QuoteIcon} alt='Quote' />
                    <p>"{testimonial.quote}"</p>
                    <div className="role">
                      <img src={testimonial.image} alt='User-Image' />
                      <div className="name">
                        <h4>{testimonial.name}</h4>
                        <small>{testimonial.role}</small>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          </section>
        </section>
      </section>
    </>
  )
}

export default About
